mapper.select:
select DATE_FORMAT(order_time, '%Y-%m-%d') as date, sum(amount) as turnover
from orders
where order_time between #{begin} and #{end}
and status = #{status}
group by DATE_FORMAT(order_time, '%Y-%m-%d') /* 按天分组 */

select count(id) from orders


        and order_time >= #{begin}


        and order_time <= #{end}


        and status = #{status}

select sum(amount) from orders


        and order_time >= #{begin}


        and order_time <= #{end}


        and status = #{status}
